import { combineReducers } from 'redux';

const valuesReducer = (state = {value: 0}, action) => {
	if (action === ADD_ONE)	state.value += 1;
	return state
}
const rootReducer = combineReducers({
	values: valuesReducer
})

export default rootReducer;
